import React from "react";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import api from "../utils/api";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup.js";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function App() {
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });
  }

  // Открытие и закрытие попапов
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setisAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setisEditAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [currentUser, setCurrentUser] = React.useState("");

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((userInfo) => {
        setCurrentUser(userInfo);
      })
      .catch((err) => console.error(err));
  }, []);

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setisAddPlacePopupOpen(false);
    setisEditAvatarPopupOpen(false);
    setSelectedCard({});
  }

  return (
    <div className="App">
      <CurrentUserContext.Provider value={currentUser}>
        <div className="root">
          <div className="page">
            <Header />
            <Main
              onEditProfile={() => setIsEditProfilePopupOpen(true)}
              onAddPlace={() => setisAddPlacePopupOpen(true)}
              onEditAvatar={() => setisEditAvatarPopupOpen(true)}
              onCardClick={(card) => setSelectedCard(card)}
            />
            <Footer />
            <PopupWithForm
              name="edit"
              title="Редактировать профиль"
              buttonValue="Сохранить"
              isOpen={isEditProfilePopupOpen}
              onClose={closeAllPopups}
            >
              <input
                required
                type="text"
                minLength="2"
                maxLength="40"
                placeholder="Напишите ваше имя"
                className="popup__input-text popup__input-text_type_name"
                id="popup__name-input"
                name="name"
              />
              <span className="popup__input-error popup__name-input-error"></span>
              <input
                required
                type="text"
                minLength="2"
                maxLength="200"
                placeholder="Расскажите о себе"
                className="popup__input-text popup__input-text_type_job"
                id="popup__job-input"
                name="about"
              />
              <span className="popup__input-error popup__job-input-error"></span>
            </PopupWithForm>
            <PopupWithForm
              name="add"
              title="Новое место"
              buttonValue="Создать"
              isOpen={isAddPlacePopupOpen}
              onClose={closeAllPopups}
            >
              <input
                required
                type="text"
                minLength="2"
                maxLength="30"
                placeholder="Название"
                className="popup__input-text popup__input-text_type_heading"
                id="popup__heading-input"
                name="name"
              />
              <span className="popup__input-error popup__heading-input-error"></span>
              <input
                required
                type="url"
                placeholder="Ссылка на картинку"
                className="popup__input-text popup__input-text_type_link"
                id="popup__link-input"
                name="link"
              />
              <span className="popup__input-error popup__link-input-error"></span>
            </PopupWithForm>
            <PopupWithForm
              name="avatar"
              title="Обновить аватар"
              buttonValue="Сохранить"
              isOpen={isEditAvatarPopupOpen}
              onClose={closeAllPopups}
            >
              <input
                required
                type="url"
                placeholder="Укажите ссылку"
                className="popup__input-text popup__input-text_type_link"
                id="popup__avatar-input"
                name="avatar"
              />
              <span className="popup__input-error popup__avatar-input-error"></span>
            </PopupWithForm>
            <ImagePopup isOpen={selectedCard} onClose={closeAllPopups} />
          </div>
        </div>
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
