import React from "react";
import { Link } from "react-router-dom";
import useForm from "../hooks/useForm";
import auth from "../utils/auth";

function Register({}) {
  const { values, handleChange, setValues } = useForm({ email: "", password: "" });

  function handleSubmit(e) {
    e.preventDefault();

    auth.registerUser(values.name, values.description);
  }

  function register() {
    auth.registerUser();
  }

  return (
    <div className="sign">
      <p className="sign__title">Регистрация</p>
      <form className="sign__form" onSubmit={handleSubmit}>
        <input
          className="sign__input"
          placeholder="Email"
          required
          id="username"
          name="username"
          type="text"
          onChange={handleChange}
        />
        <input
          className="sign__input"
          placeholder="Пароль"
          required
          id="password"
          name="password"
          type="password"
          onChange={handleChange}
        />
        <input type="submit" className="sign__button" value="Зарегистрироваться" />
      </form>
      <div className="sign__redirect">
        <p className="sign__link">Уже зарегистрированы?⠀</p>
        <Link to="/signin" className="sign__link">
          Войти
        </Link>
      </div>
    </div>
  );
}

export default Register;
