import avatar from "../images/Avatar.jpg";
import React from "react";
import "../index.css";

function handleEditAvatarClick() {
  document.querySelector(".popup-avatar").classList.add("popup_opened");
}
const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
  React.useEffect(false);

function handleEditProfileClick() {
  document.querySelector(".popup-edit").classList.add("popup_opened");
}

function handleAddPlaceClick() {
  document.querySelector(".popup-add").classList.add("popup_opened");
}

function Main() {
  return (
    <main>
      <section className="profile">
        <div className="profile__user">
          <button
            className="profile__edit-avatar"
            type="button"
            onClick={handleEditAvatarClick}
          >
            <img className="profile__avatar" src={avatar} alt="Аватар" />
          </button>
          <div className="profile__user-info">
            <div className="profile__edit-name">
              <h1 className="profile__user-name">Жак-Ив Кусто</h1>
              <button
                className="profile__edit-button"
                type="button"
                onClick={handleEditProfileClick}
              />
            </div>
            <p className="profile__user-describe">Исследователь океана</p>
          </div>
        </div>
        <button
          className="profile__add-button"
          type="button"
          onClick={handleAddPlaceClick}
        />
      </section>
      <section className="cards">
        <ul className="elements" />
      </section>
    </main>
  );
}

export default Main;
