import React from "react";
import { AppContext } from "../contexts/AppContext";

function InfoTooltip({ showInfo, isRegister }) {
  const { isLoading, closeAllPopups } = React.useContext(AppContext);

  return (
    <div className={`popup popup_opened`}>
      <div className="popup__container">
        <button className="popup__exit" type="button" onClick={closeAllPopups} />
        <div className={`${isRegister && "popup__success"} ${!isRegister && "popup__fail"}`}></div>
        <h2 className="popup__title">{`${isRegister && "Вы успешно зарегистрировались!"} ${
          !isRegister && "Что-то пошло не так! Попробуйте ещё раз."
        }`}</h2>
      </div>
    </div>
  );
}

export default InfoTooltip;
