import React from "react";
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup({ isOpen, onAddPlace }) {
  const {
    values: name,
    handleChange: handleChangeName,
    setValues: setName,
  } = useForm({ name: "" });
  const {
    values: link,
    handleChange: handleChangeLink,
    setValues: setLink,
  } = useForm({ link: "" });

  React.useEffect(() => {
    setName("");
    setLink("");
  }, [isOpen]);

  function handleSubmit(e) {
    e.preventDefault();

    onAddPlace({
      name: name.name,
      link: link.link,
    });
  }

  return (
    <PopupWithForm
      name="add"
      title="Новое место"
      buttonValue="Создать"
      isOpen={isOpen}
      onSubmit={handleSubmit}
    >
      <input
        required
        type="text"
        minLength="2"
        maxLength="30"
        placeholder="Название"
        className="popup__input-text popup__input-text_type_heading"
        id="popup__heading-input"
        name="name"
        onChange={handleChangeName}
        value={name.name}
      />
      <span className="popup__input-error popup__heading-input-error"></span>
      <input
        required
        type="url"
        placeholder="Ссылка на картинку"
        className="popup__input-text popup__input-text_type_link"
        id="popup__link-input"
        name="link"
        onChange={handleChangeLink}
        value={link.link}
      />
      <span className="popup__input-error popup__link-input-error"></span>
    </PopupWithForm>
  );
}

export default AddPlacePopup;
