import React from "react";
import "../index.css";

function ImagePopup(props) {
  console.log(props.isOpen);
  return (
    <div className={`popup image-popup ${props.selectedCard ? "popup_opened" : ""}`}>
      <div className="image-popup__container">
        <figure className="image-popup__figure">
          <img src={props.link} alt={props.title} className="image-popup__image" />
          <figcaption className="image-popup__caption" />
        </figure>
        <button className="popup__exit image-popup__exit" type="button" onClick={props.onClose} />
      </div>
    </div>
  );
}

export default ImagePopup;
