import React from "react";

function ImagePopup(props) {
  return (
    <div className={`popup image-popup ${props.isOpen ? "popup_opened" : ""}`}>
      <div className="image-popup__container">
        <figure className="image-popup__figure">
          <img src={props.isOpen.link} alt={props.isOpen.name} className="image-popup__image" />
          <figcaption className="image-popup__caption">{props.isOpen.name}</figcaption>
        </figure>
        <button className="popup__exit image-popup__exit" type="button" onClick={props.onClose} />
      </div>
    </div>
  );
}

export default ImagePopup;
