import React from "react";
import "../index.css";
import api from "../utils/Api";

function Main(props) {
  const [userName, setUserName] = React.useState("");
  const [userDescription, setUserDescription] = React.useState("");
  const [userAvatar, setUserAvatar] = React.useState("");
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((userInfo) => {
        setUserName(userInfo.name);
        setUserDescription(userInfo.about);
        setUserAvatar(userInfo.avatar);
      })
      .catch((err) => console.error(err));
  }, []);

  React.useEffect(() => {
    api
      .getInitialCards()
      .then((dataCards) => {
        setCards(dataCards);
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <main>
      <section className="profile">
        <div className="profile__user">
          <button className="profile__edit-avatar" type="button" onClick={props.onEditAvatar}>
            <img className="profile__avatar" src={userAvatar} alt="Аватар" />
          </button>
          <div className="profile__user-info">
            <div className="profile__edit-name">
              <h1 className="profile__user-name">{userName}</h1>
              <button
                className="profile__edit-button"
                type="button"
                onClick={props.onEditProfile}
              />
            </div>
            <p className="profile__user-describe">{userDescription}</p>
          </div>
        </div>
        <button className="profile__add-button" type="button" onClick={props.onAddPlace} />
      </section>
      <section className="cards">
        <ul className="elements" />
        {cards.map((card, i) => {
          <li class="elements__item">
            <button class="elements__delete" type="button"></button>
            <img class="elements__image" src={card.link} alt={card.title} />
            <div class="elements__bottom">
              <h2 class="elements__title">{card.title}</h2>
              <div class="elements__like-wrapper">
                <button class="elements__like-icon" type="button"></button>
                <p class="elements__like-counter">0</p>
              </div>
            </div>
          </li>;
        })}
      </section>
    </main>
  );
}

export default Main;
