import React from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

function EditProfilePopup({ isOpen, onClose, onUpdateUser }) {
  const currentUser = React.useContext(CurrentUserContext);
  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");

  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [currentUser, isOpen]);

  function handleChangeName(e) {
    setName(e.target.value);
  }
  function handleChangeDescription(e) {
    setDescription(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();

    onUpdateUser({
      name,
      about: description,
    });
  }

  return (
    <PopupWithForm
      name="edit"
      title="Редактировать профиль"
      buttonValue="Сохранить"
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
    >
      <input
        required
        type="text"
        minLength="2"
        maxLength="40"
        placeholder="Напишите ваше имя"
        className="popup__input-text popup__input-text_type_name"
        id="popup__name-input"
        name="name"
        onChange={handleChangeName}
        value={name}
      />
      <span className="popup__input-error popup__name-input-error"></span>
      <input
        required
        type="text"
        minLength="2"
        maxLength="200"
        placeholder="Расскажите о себе"
        className="popup__input-text popup__input-text_type_job"
        id="popup__job-input"
        name="about"
        onChange={handleChangeDescription}
        value={description}
      />
      <span className="popup__input-error popup__job-input-error"></span>
    </PopupWithForm>
  );
}

export default EditProfilePopup;
